import pandas as pdimport pytestimport tce_calcdef test_tc2():    ds = '2021-10-08'    dd = {'FlatRate': 12.31,          'WorldScale': 100.0,          'MGO': 705.25,          'VLSFO': 565.25,          'HSFO': 560.25,          }    data = pd.DataFrame(dd, index=[pd.to_datetime(ds)])    res = tce_calc.calc('TC2_37', data)    assert res['GrossFreight'][ds] == pytest.approx(486042.91, abs=1e-1)    assert res['BunkerCost'][ds] == pytest.approx(413495.95, abs=1e-1)    assert res['TCE'][ds] == pytest.approx(-1436.96, abs=1e-1)def test_tc5():    ds = '2021-10-06'    dd = {        'FlatRate': 20.46,        'WorldScale': 103.14,        'MGO': 355.001,        'VLSFO': 577.27,        'HSFO': 225.79,    }    data = pd.DataFrame(dd, index=[pd.to_datetime(ds)])    res = tce_calc.calc('tc5', data)    assert res['GrossFreight'][ds] == pytest.approx(1175484.42, abs=1e-1)    assert res['BunkerCost'][ds] == pytest.approx(846632.20, abs=1e-1)    assert res['TCE'][ds] == pytest.approx(4137.76, abs=1e-1)